var navToggle = function(){
    var toggleElem = document.querySelector('.site-navtoggle');
    var toggleTarget = document.querySelector('.site-header');
    var toggleClass = 'state--navactive';
    toggleElem.addEventListener('click',function(){
        if(toggleTarget.classList.contains(toggleClass)){
            toggleTarget.classList.remove(toggleClass);
        }else{
            toggleTarget.classList.add(toggleClass);
        }
    });
};

navToggle();

var square = document.createElement('div');
square.setAttribute('style' , 'background: red; width: 100px; height: 100px; position: absolute; top: 0; left: 0');
document.body.appendChild(square);

document.addEventListener('mousemove',function(e){
    setTimeout(function(){
        square.setAttribute('style', 'top:' + e.y + 'px; left:' + e.x + 'px')
    },1000);
});


define("app", function(){});
